/* selectCertificateList */ SELECT A.CERTIFICATE_CAT , B.CODE_NM AS CERTIFICATE_CAT_NM , A.CERTIFICATE_TITLE , A.CERTIFICATE_DESC , A.CERTIFICATE_DAY , A.MAJOR_YN , (SELECT FILE_USER_NM FROM TB_FILE F WHERE F.FILE_DATA_SN = A.CERTIFICATE_SN AND F.USE_STATE = 'Y' AND F.FILE_CONTENT_CD = 'thumb') AS FILE_USER_NM_THUMB , (SELECT FILE_PATH FROM TB_FILE F WHERE F.FILE_DATA_SN = A.CERTIFICATE_SN AND F.USE_STATE = 'Y' AND F.FILE_CONTENT_CD = 'thumb') AS FILE_PATH_THUMB , (SELECT FILE_USER_NM FROM TB_FILE F WHERE F.FILE_DATA_SN = A.CERTIFICATE_SN AND F.USE_STATE = 'Y' AND F.FILE_CONTENT_CD = 'main') AS FILE_USER_NM_MAIN , (SELECT FILE_PATH FROM TB_FILE F WHERE F.FILE_DATA_SN = A.CERTIFICATE_SN AND F.USE_STATE = 'Y' AND F.FILE_CONTENT_CD = 'main') AS FILE_PATH_MAIN FROM TB_CERTIFICATE A INNER JOIN TB_CODE B ON (B.CODE_ID = A.CERTIFICATE_CAT) WHERE A.USE_STATE = 'Y' AND B.USE_STATE = 'Y' AND A.CERTIFICATE_ID = #{certificate_id}
AND A.CERTIFICATE_CAT = #{certificate_cat}
AND A.MAJOR_YN = #{major_yn}
AND A.CERTIFICATE_CAT IN
#{item}
ORDER BY A.SORT_NO ASC
